import logo from './logo.svg';
import './App.css';
import { getSpaceUntilMaxLength } from '@testing-library/user-event/dist/utils';

const uuid = require('uuid');

function App() {

  const [image, setImage] = useState('');
  const [uploadResultMessage, setUploadResultMessage] = useState('Please upload an image to authenication.')
  const [visitorName, setVisitorName] = useState('logo.svg');
  const [isAuth, setAuth] = useState(false);

  function sendImage(e){
    e.preventDefault();
    setVisitorName(image.name);
    const visitorImageName = uuid.v4();
    fetch(`https://42bt4wcvl8.execute-api.us-east-2.amazonaws.com/dev/analyze-people-data/{visitorImageName}.jpeg`,{
    method: 'PUT',
    headers: {
      'Content-Type': 'image/jpeg'

    },
    body: image})
    // .then( async () => {
    //   const reponse = await authenticate(visitorImageName);
    //   if (reponse.method === 'Success'){
    //     setUploadResultMessage(`Hi ${reponse['firstName']} ${reponse['lastName']}`)
    //     setAuth(true)
    //   }else {
    //     setUploadResultMessage('Authentication Failed')
    //     setAuth(false)
    //   }
    // }).catch(error => {
    //   setAuth(false);
    //   setUploadResultMessage('There is an error during the authentication proces.')
    //   console.error(error);
    // })
  }

  async function authenticate(visitorImageName) {
    const requestUrl = 'https://42bt4wcvl8.execute-api.us-east-2.amazonaws.com/dev/people' + new URLSearchParams({
      objectKey: `${visitorImageName}.jpeg`
    });
    return await fetch(requestUrl, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    }).then((data) => {
      return data;
    }).catch(error => console.error(error));
  }

  return ( 
    <div className="App">
      <h2>Facial Recognition systm</h2>
      <form onSubmit={sendImage}>
        <input file='file' name='image' onChange={e => setImage(e.target.files[0])}/>
        <button type='submit'>Authenticate</button>
      </form>
      <div className={isAuth ? 'success' : 'failure'}>{uploadResultMessage}</div>
      <img src={ require(`./visitors/${visitorName}`)} alt="Visitor" height={250} width={250} />

    </div>
  );
}

export default App;
